from collections import deque

q = int(input())
queue = deque()

for _ in range(q):
    operation = input().split()

    if operation[0] == "push":
        queue.append(int(operation[1]))
    elif operation[0] == "pop":
        queue.popleft()
    elif operation[0] == "front":
        print(queue[0])
